   png_memcpy(buf + wlen + 2, height, hlen);  /* Do NOT append the '\0' here */
   png_debug1(3, "sCAL total length = %u", (unsigned int)total_len);
   png_write_chunk(png_ptr, (png_bytep)png_sCAL, buf, total_len);
