    tmp0 = MULTIPLY90(z1 - z2, FIX(0.707106781)); /* c6 */
    tmp11 = tmp2 + tmp0;
    tmp14 = tmp2 - tmp0 - tmp0;
    tmp0 = MULTIPLY90(z1 + z2, FIX(1.328926049)); /* c2 */
    tmp2 = MULTIPLY90(z1, FIX(1.083350441));      /* c4 */
    tmp3 = MULTIPLY90(z2, FIX(0.245575608));      /* c8 */
    tmp10 = tmp1 + tmp0 - tmp3;
