    z2 = MULTIPLY90(z4, FIX(1.274162392));         /* c4 */
    z3 = MULTIPLY90(z4, FIX(0.314692123));         /* c12 */
    z4 = MULTIPLY90(z4, FIX(0.881747734));         /* c8 */
