    tmp12 = z1 - z4;
    tmp23 = z1 - ((z2 + z3 - z4) << 1);          /* c0 = (c4+c12-c8)*2 */
    z1 = (INT32) wsptr[2];
    z2 = (INT32) wsptr[6];
    z3 = MULTIPLY90(z1 + z2, FIX(1.105676686));    /* c6 */
    tmp13 = z3 + MULTIPLY90(z1, FIX(0.273079590)); /* c2-c6 */
