int BZ_API(sp_BZ2_bzBuffToBuffCompress)
                         ( char*         dest,
                           unsigned int* destLen,
                           char*         source,
                           unsigned int  sourceLen,
                           int           blockSize100k,
                           int           verbosity,
                           int           workFactor )
{
   bz_stream strm;
   int ret;
   if (dest == NULL || destLen == NULL ||
       source == NULL ||
       blockSize100k < 1 || blockSize100k > 9 ||
       verbosity < 0 || verbosity > 4 ||
       workFactor < 0 || workFactor > 250)
      return BZ_PARAM_ERROR;
   if (workFactor == 0) workFactor = 30;
   strm.bzalloc = NULL;
   strm.bzfree = NULL;
   strm.opaque = NULL;
   ret = sp_BZ2_bzCompressInit ( &strm, blockSize100k,
                              verbosity, workFactor );
   if (ret != BZ_OK) return ret;
   strm.next_in = source;
   strm.next_out = dest;
   strm.avail_in = sourceLen;
   strm.avail_out = *destLen;
   ret = sp_BZ2_bzCompress ( &strm, BZ_FINISH );
   if (ret == BZ_FINISH_OK) goto output_overflow;
   if (ret != BZ_STREAM_END) goto errhandler;
   /* normal termination */
   *destLen -= strm.avail_out;
   sp_BZ2_bzCompressEnd ( &strm );
   return BZ_OK;
   output_overflow:
   sp_BZ2_bzCompressEnd ( &strm );
   return BZ_OUTBUFF_FULL;
   errhandler:
   sp_BZ2_bzCompressEnd ( &strm );
   return ret;
}
/*---------------------------------------------------*/
int BZ_API(sp_BZ2_bzBuffToBuffDecompress)
                           ( char*         dest,
                             unsigned int* destLen,
                             char*         source,
                             unsigned int  sourceLen,
                             int           bsmall,
                             int           verbosity )
{
   bz_stream strm;
   int ret;
   if (dest == NULL || destLen == NULL ||
       source == NULL ||
       (bsmall != 0 && bsmall != 1) ||
       verbosity < 0 || verbosity > 4)
          return BZ_PARAM_ERROR;
   strm.bzalloc = NULL;
   strm.bzfree = NULL;
   strm.opaque = NULL;
   ret = sp_BZ2_bzDecompressInit ( &strm, verbosity, bsmall );
   if (ret != BZ_OK) return ret;
   strm.next_in = source;
   strm.next_out = dest;
   strm.avail_in = sourceLen;
   strm.avail_out = *destLen;
   ret = sp_BZ2_bzDecompress ( &strm );
   if (ret == BZ_OK) goto output_overflow_or_eof;
   if (ret != BZ_STREAM_END) goto errhandler;
   /* normal termination */
   *destLen -= strm.avail_out;
   sp_BZ2_bzDecompressEnd ( &strm );
   return BZ_OK;
   output_overflow_or_eof:
   if (strm.avail_out > 0) {
      sp_BZ2_bzDecompressEnd ( &strm );
      return BZ_UNEXPECTED_EOF;
   } else {
      sp_BZ2_bzDecompressEnd ( &strm );
      return BZ_OUTBUFF_FULL;
   };
   errhandler:
   sp_BZ2_bzDecompressEnd ( &strm );
   return ret;
}
